    final static int CHAR_COUNT = 26;

    public static List<Integer> findSubstring(String s, String[] words) {
        int n = s.length();
        int m = words.length;
        int k = words[0].length();
        int len = m * k;
        List<Integer> match = new ArrayList<>();
        if(n < len) return match;
        int[] wordsCharCount = new int[CHAR_COUNT];
        HashMap<String, Integer> wordsMap = new HashMap<>();
        for(int i=0; i<m; i++){
            String word = words[i];
            if(!wordsMap.containsKey(word)) wordsMap.put(word, 0);
            wordsMap.put(word, wordsMap.get(word) + 1);
            for(int j=0; j<k; j++){
                int c = word.charAt(j) - 'a';
                wordsCharCount[c] += 1;
            }
        }
        String wordsHash = getHash(wordsCharCount);
        int[] count = new int[CHAR_COUNT];
        for(int i=0; i<len; i++){
            int c = s.charAt(i) - 'a';
            count[c] += 1;
        }
        String windowHash = getHash(count);
        if(wordsHash.equals(windowHash)){
            String window = s.substring(0, len);
            if(checkWords(window, wordsMap, m, k)){
                match.add(0);
            }
        }

        for(int i=len; i<n; i++){
            int cs = s.charAt(i-len) - 'a';
            int ce = s.charAt(i) - 'a';
            count[cs] -= 1;
            count[ce] += 1;
            windowHash = getHash(count);
            if(wordsHash.equals(windowHash)){
                String window = s.substring(i-len+1, i+1);
                if(checkWords(window, wordsMap, m, k)){
                    match.add(i-len+1);
                }
            }
        }
        return match;
    }
    private static boolean checkWords(String window, HashMap<String, Integer> wordsMap, int m, int k){
        HashMap<String, Integer> hashMap = new HashMap<>();
        for(int z=0; z<m; z++){
            int start = z*k;
            int end = (z+1)*k;
            String sub = window.substring(start, end);
            if(wordsMap.containsKey(sub)){
                if(!hashMap.containsKey(sub)) hashMap.put(sub, 0);
                hashMap.put(sub, hashMap.get(sub) + 1);
                if(z == m-1){
                    if(compareMaps(wordsMap, hashMap)){
                        return true;
                    }
                }
            }
            else break;
        }
        return false;
    }

    private static String getHash(int[] arr){
        StringBuilder hash = new StringBuilder();
        for(int val : arr){
            hash.append(val + ":");
        }
        return hash.toString();
    }

    private static boolean compareMaps(HashMap<String, Integer> map1, HashMap<String, Integer> map2){
        for(String key : map1.keySet()){
            int a = map1.get(key);
            int b = map2.get(key) == null ? -1 : map2.get(key) ;
            if(a != b){
                return false;
            }
        }
        return true;
    }
